    x[3] = x[2] - yy;
    y[3] = y[2] + xx;
    x[4] = x[3] - xx;
    y[4] = y[3] - yy;
  } else if (x[1] <= x[2] && y[1] >= y[2]) {
    x[3] = x[2] + yy;
    y[3] = y[2] + xx;
    x[4] = x[3] - xx;
    y[4] = y[3] + yy;
  } else if (x[1] >= x[2] && y[1] <= y[2]) {
    x[3] = x[2] - yy;
    y[3] = y[2] - xx;
    x[4] = x[3] + xx;
    y[4] = y[3] - yy;
  }
  printf("%d %d %d %d\n", x[3], y[3], x[4], y[4]);
  return 0;
