//
// Created by Takaaki Hirano on 2018/09/01.
//
#include <algorithm>
#include <cmath>
#include <cstdio>
#include <iostream>
#include <map>
#include <unordered_map>
#include <vector>

using namespace std;
int N, K;
long long ans = 0;

void solve() {
  unordered_map<long long, long long> mp;
  for (int i = 1; i <= N; ++i) {
    mp[i % K] = mp[i % K] + 1;
  }
  for (auto mppair : mp) {
    for (auto mppari2 : mp) {
      if ((mppair.first + mppari2.first) % K == 0) {
        for (auto mppair3 : mp) {
          if ((mppair.first + mppair3.first) % K == 0 &&
              (mppari2.first + mppair3.first) % K == 0) {
            ans += mppair.second * mppari2.second * mppair3.second;
          }
        }
      }
    }
  }

  printf("%lli\n", ans);
}

int main() {
  cin >> N >> K;
  solve();
  return 0;
}

