#include "iostream"
#include "vector"

using namespace std;
typedef long long ll;

const int MAX = 510000;
const int MOD = 1000000007;

long long fac[MAX], finv[MAX], inv[MAX];

// テーブルを作る前処理
void COMinit() {
  fac[0] = fac[1] = 1;
  finv[0] = finv[1] = 1;
  inv[1] = 1;
  for (int i = 2; i < MAX; i++) {
    fac[i] = fac[i - 1] * i % MOD;
    inv[i] = MOD - inv[MOD % i] * (MOD / i) % MOD;
    finv[i] = finv[i - 1] * inv[i] % MOD;
  }
}

// 二項係数計算
long long COM(int n, int k) {
  if (n < k)
    return 0;
  if (n < 0 || k < 0)
    return 0;
  return fac[n] * (finv[k] * finv[n - k] % MOD) % MOD;
}

int main() {
  COMinit();
  ll n, k;
  cin >> n >> k;

  vector<ll> mods(min(k, n), 0);

  for (auto mod = 0; mod <= min(k, n); mod++) {
    mods[mod] = mod == 0 ? (n - mod) / k : (n - mod) / k + 1;
  }

  ll cnt = mods[0] * mods[0] * mods[0];
  if (k % 2 == 0 && k / 2 <= n) {
    cnt += mods[k / 2] * mods[k / 2] * mods[k / 2];
  }

  cout << cnt << endl;

  return 0;
}

