  if (id[x])
    return;
  if (!x) {
    id[x] = ++n;
    return;
  }
  if (x & 1) {
    dfs(x / 2);
    if (!id[x])
      id[x] = ++n;
    Add(x / 2, x, x / 2 + 1);
    Add(x / 2, x, 0);
  } else {
    dfs(x / 2 - 1);
    if (!id[x])
      id[x] = ++n;
    Add(x / 2 - 1, x, x / 2);
    Add(x / 2 - 1, x, 0);
    Add(0, x, x);
  }
}
int main() {
  scanf("%d", &l);
  l--;
  dfs(l);
  printf("%d %d\n", n, m);
  for (int i = 1; i <= m; i++)
    printf("%d %d %d\n", u[i], v[i], w[i]);
}

