#include <vector>

#define int long long
using namespace std;
#define rep(i, n) for (int i = 0; i < (n); ++i)
typedef pair<int, int> pii;
const int INF = 1l << 60;
#define u_b upper_bound
#define l_b lower_bound

int L;
int from[66], to[66], cost[66];
int edgecnt;

int bitlen(int x) {
  int res = 0;
  while (x) {
    res++;
    x /= 2;
  }
  return res;
}

void e1(int N) {
  int k = 1;
  for (int i = N; i >= 3; --i) {
    from[edgecnt] = i - 1;
    to[edgecnt] = i;
    cost[edgecnt] = k;
    from[edgecnt + 1] = i - 1;
    to[edgecnt + 1] = i;
    cost[edgecnt + 1] = 0;
    k *= 2;
    edgecnt += 2;
  }
}

int leftbit(int x) {
  if (x == 0)
    return 0;
  int res = 1 << 30;
  while (res) {
    if (x & res)
      return res;
    res >>= 1;
  }
  return -1;
}

int e2(int x) {
  int res = 0;
  rep(res, 100) {
    if (x == (1 << res))
      return res;
  }
}

signed main() {
  cin >> L;
  int N = bitlen(L) + 1;
  e1(N);
  L ^= leftbit(L);
  int c = (1 << (N - 2));
  while (L) {
    int l = leftbit(L);
    L ^= l;
    from[edgecnt] = 1;
    to[edgecnt] = N - e2(l);
    cost[edgecnt] = c;
    c += l;
    edgecnt++;
  }
  if (N > 20 || edgecnt > 60)
    return 1;
  rep(i, edgecnt) {
    if (from[i] > 1)
      from[i]--;
    if (to[i] > 1)
      to[i]--;
  }
  cout << N - 1 << " " << edgecnt << endl;
  rep(i, edgecnt) { cout << from[i] << " " << to[i] << " " << cost[i] << endl; }
  return 0;
}

