  int k, n;
  cin >> k >> n;

  repA(i, 2, 2 * k) {
    ll ans = 0, h, m;

    if (i > k)
      h = (2 * k + 1 - i) / 2, m = i - k - 1 + h;
    else
      h = (i - 1) / 2, m = k - i + 1 + h;

    repA(j, 0, h) ans +=
        f(n + m - h - 1, m - h + j - 1) * p[j] % mod * f(h, h - j) % mod,
        ans %= mod;

    n--;
    if (i % 2 == 0)
      repA(j, 0, h) ans +=
          f(n + m - h - 1, m - h + j - 1) * p[j] % mod * f(h, h - j) % mod,
          ans %= mod;
    n++;

