#include <bits/stdc++.h>
using namespace std;

#define int long long

#define pb push_back
#define mp make_pair
#define st first
#define nd second
typedef pair<int, int> pp;
const int mod = 998244353;
const int N = 2e3 + 3;

int C[N][N], M[N][N][3];

int f(int i, int k, int h) {
  if (i == 0)
    return k == 0;
  int &r = M[i][k][h];
  if (r != -1)
    return r;
  r = f(i - 1, k, 0);
  if (k == 0)
    return r;

  if (h == 0)
    r = (r + f(i, k - 1, 1) + f(i, k - 1, 2)) % mod;
  if (h == 1)
    r = (r + f(i, k - 1, 1)) % mod;
  if (h == 2)
    r = (r + f(i, k - 1, 2)) % mod;
  return r;
}

int g(int a, int b) {
  if (a < 0 || b < 0)
    return 0;
  if (a == 0)
    return 1;
  if (b == 0)
    return 0;
  return C[a + b - 1][b - 1];
}

signed main() {
  int n, k;
  cin >> k >> n;
  for (int i = 0; i < N; i++)
    for (int j = C[i][0] = 1; j <= i; j++)
      C[i][j] = (C[i - 1][j] + C[i - 1][j - 1]) % mod;

  memset(M, -1, sizeof M);
  for (int i = 2; i <= k + k; i++) {
    int ans = 0;
    for (int j = 0; j <= n; j++) {
      if (i & 1) {
        int mn = min(i / 2, k - i / 2);
        ans = (ans + f(mn, j, 0) * g(n - j, k - 2 * mn)) % mod;
      } else {
        int mn = min(i / 2 - 1, k - i / 2);
        ans = (ans + f(mn, j, 0) * g(n - j, k - 2 * mn - 1)) % mod;
        if (j < n)
          ans = (ans + f(mn, j, 0) * g(n - j - 1, k - 2 * mn - 1)) % mod;
      }
    }

    cout << ans << endl;
  }

  return 0;
}

