};

template <class T> bool nibu(const graph<T> &g) {
  if (g.edges.size() == 0)
    return true;
  UnionFind uf(g.n * 2);
  for (auto &&e : g.edges)
    uf.unite(e.from, e.to + g.n), uf.unite(e.from + g.n, e.to);
  rep(i, g.n) if (uf.same(i, i + g.n)) return 0;
  return 1;
}
// 二部グラフを色分けした際の頂点数を返す
template <class T> vp nibug(graph<T> &g) {
  vp cg;
  if (!nibu(g))
    ole();
  int _n = g.size();
  vb _was(_n);
  queue<P> q;
  rep(i, _n) {
    if (_was[i])
      continue;
    q.push(mp(i, 1));
    _was[i] = 1;
    int red = 0;
    int coun = 0;
    while (q.size()) {
      int now = q.front().F;
      int col = q.front().S;
      red += col;
      coun++;
      q.pop();
      forg(gi, g[now]) {
        if (_was[t])
          continue;
        q.push(mp(t, col ^ 1));
        _was[t] = 1;
      }
    }
    cg.push_back(mp(red, coun - red));
  }
  return cg;
}

template <class T> vector<T> dijkstra(const graph<T> &g, int s) {
  if (!(0 <= s && s < g.n))
    ole();
  T initValue = MAX(T);
  vector<T> dis(g.n, initValue);
  priority_queue<pair<T, int>, vector<pair<T, int>>, greater<pair<T, int>>> q;
  dis[s] = 0;
  q.emplace(0, s);
  while (q.size()) {
    T nowc = q.top().F;
    int i = q.top().S;
    q.pop();
    if (dis[i] != nowc)
      continue;
    for (auto &&e : g.g[i]) {
      int to = e.to;
      T cost = nowc + e.cost;
      if (dis[to] > cost) {
        dis[to] = cost;
        q.emplace(dis[to], to);
      }
    }
  }
  // たどり着かないなら-1
  for (auto &&d : dis)
    if (d == initValue)
      d = -1;
  return dis;
}

