  int size() { return g.size(); }
  bool isleaf(int v) {
    assert(root != -1);
    return g[v].size() == 1 && g[v][0].from != root;
  }
  vector<edge<T>> &operator[](int i) { return g[i]; }
  virtual void add(int from, int to, T cost, int ty) = 0;
  virtual bool used(edge<T> &e) = 0;
  virtual bool used(int id) = 0;
  virtual void del(edge<T> &e) = 0;
  virtual void del(int id) = 0;
};

template <class T = int> class undigraph : public graph<T> {
public:
  using graph<T>::g;
  using graph<T>::n;
  using graph<T>::edges;
  using graph<T>::_used;
  undigraph(int n) : graph<T>(n) {}
  void add(int f, int t, T cost = 1, int ty = -1) {
    if (!(0 <= f && f < n && 0 <= t && t < n))
      ole();
    int id = edges.size();
    g[f].emplace_back(f, t, cost, id, ty);
    g[t].emplace_back(t, f, cost, id + 1, ty);
    edges.emplace_back(f, t, cost, id, ty);
    edges.emplace_back(t, f, cost, id + 1, ty);
  }
  void add(edge<T> &e) {
    int f = e.from, t = e.to, ty = e.type;
    T cost = e.cost;
    add(f, t, cost, ty);
  }
  bool used(edge<T> &e) { return _used[e.id]; }
  bool used(int id) { return _used[id]; }
  void del(edge<T> &e) { _used[e.id] = _used[e.id ^ 1] = 1; }
  void del(int id) { _used[id] = _used[id ^ 1] = 1; }
};

template <typename T = ll> class digraph : public graph<T> {
public:
  using graph<T>::g;
  using graph<T>::n;
  using graph<T>::edges;
  using graph<T>::_used;

  digraph(int n) : graph<T>(n) {}
  void add(int f, int t, T cost = 1, int ty = -1) {
    if (!(0 <= f && f < n && 0 <= t && t < n))
      ole();
    int id = edges.size();
    g[f].emplace_back(f, t, cost, ty, id);
    edges.emplace_back(f, t, cost, ty, id);
  }
  bool used(edge<T> &e) { return _used[e.id]; }
  bool used(int id) { return _used[id]; }
  void del(edge<T> &e) { _used[e.id] = _used[e.id ^ 1] = 1; }
  void del(int id) { _used[id] = _used[id ^ 1] = 1; }
};

template <class T> bool nibu(const graph<T> &g) {
  if (g.edges.size() == 0)
    return true;
  UnionFind uf(g.n * 2);
  for (auto &&e : g.edges)
    uf.unite(e.from, e.to + g.n), uf.unite(e.from + g.n, e.to);
  rep(i, g.n) if (uf.same(i, i + g.n)) return 0;
  return 1;
}
// 二部グラフを色分けした際の頂点数を返す
template <class T> vp nibug(graph<T> &g) {
  vp cg;
  if (!nibu(g))
    ole();
  int _n = g.size();
  vb _was(_n);
  queue<P> q;
  rep(i, _n) {
    if (_was[i])
      continue;
    q.push(mp(i, 1));
    _was[i] = 1;
    int red = 0;
    int coun = 0;
    while (q.size()) {
      int now = q.front().F;
      int col = q.front().S;
      red += col;
      coun++;
      q.pop();
      forg(gi, g[now]) {
        if (_was[t])
          continue;
        q.push(mp(t, col ^ 1));
        _was[t] = 1;
      }
    }
    cg.push_back(mp(red, coun - red));
  }
  return cg;
}

template <class T> vector<T> dijkstra(const graph<T> &g, int s) {
  if (!(0 <= s && s < g.n))
    ole();
  T initValue = MAX(T);
  vector<T> dis(g.n, initValue);
  priority_queue<pair<T, int>, vector<pair<T, int>>, greater<pair<T, int>>> q;
  dis[s] = 0;
  q.emplace(0, s);
  while (q.size()) {
    T nowc = q.top().F;
    int i = q.top().S;
    q.pop();
    if (dis[i] != nowc)
      continue;
    for (auto &&e : g.g[i]) {
      int to = e.to;
      T cost = nowc + e.cost;
      if (dis[to] > cost) {
        dis[to] = cost;
        q.emplace(dis[to], to);
      }
    }
  }
  // たどり着かないなら-1
  for (auto &&d : dis)
    if (d == initValue)
      d = -1;
  return dis;
}

