template <typename T> T mypow(T a, T b) {
  if (b == 0)
    return 1;
  T tmp = mypow(a, b / 2);
  if (b % 2)
    return (((tmp * tmp) % MOD) * a) % MOD;
  else
    return (tmp * tmp) % MOD;
}

// v[i][j] := iCj
std::vector<std::vector<ll>> comb2(int n) {
  std::vector<std::vector<ll>> v(n + 1, std::vector<ll>(n + 1, 0));
  for (int i = 0; i < (int)v.size(); i++) {
    v[i][0] = 1;
    v[i][i] = 1;
  }
  for (int j = 1; j < (int)v.size(); j++) {
    for (int k = 1; k < j; k++) {
      v[j][k] = (v[j - 1][k - 1] + v[j - 1][k]);
      v[j][k] %= MOD;
    }
  }
  return v;
}

auto comb = comb2(4040);

// nHk = n+k-1 C k
ll H(int n, int k) {
  assert(k >= 0);
  return comb[n + k - 1][k];
}

