  cin.tie(0);
  ios::sync_with_stdio(false);

  int K, N;
  cin >> K >> N;
  for (int i = 2; i <= 2 * K; i++) {
    ll ret = 0;
    int p = 0;
    for (int j = 1; 2 * j <= i; j++) {
      if (K < i - j)
        continue;
      p++;
    }
    for (int q = 1; q <= p; q++) {
      ll sum = comb[p][q];
      sum = sum * H(K, N - 2 * q) % MOD;
      if (q % 2) {
        ret += sum;
        ret %= MOD;
      } else {
        ret = ret + MOD - sum;
        ret %= MOD;
      }
    }
    cout << (H(K, N) + MOD - ret) % MOD << endl;
  }
}

