  return os;
}

namespace comb {
const int N = 4040;
mint fact[N];
mint rev[N];

void init() {
  fact[0] = 1;
  for (int i = 1; i < N; ++i)
    fact[i] = fact[i - 1] * i;
  for (int i = 0; i < N; ++i)
    rev[i] = fact[i].inverse();
}

mint C(int n, int r) {
  if (n < r)
    return 0;
  return fact[n] * rev[r] * rev[n - r];
}

mint H(int n, int r) { return C(n + r - 1, r); }

mint P(int n, int r) {
  assert(n >= r);
  return fact[n] * rev[n - r];
}

mint catalan(int n) { return fact[2 * n] * rev[n + 1] * rev[n]; }
} // namespace comb

void solve() {
  comb::init();
  int K, N;
  cin >> K >> N;
  for (int i = 2; i <= 2 * K; ++i) {
    int ps = min(K, i / 2) - max(1, i - K) + 1;
    mint ans = 0;
    for (int j = 0; j <= min(N / 2, ps); ++j) {
      mint x = comb::C(ps, j) * comb::H(K, N - 2 * j);
      ans += x * (j % 2 ? -1 : 1);
    }
    cout << ans << endl;
  }
}

int main() {
  ios::sync_with_stdio(false);
  cin.tie(0);
  cout << fixed << setprecision(15);
  solve();
  return 0;
}

