  for (int i = 2; i < 200000; i++)
    fact[i] = fact[i - 1] * i % P;
  int N, K;
  cin >> K >> N;

  ll all = comb_mod(N + K - 1, K - 1, P);
  for (int t = 2; t <= 2 * K; t++) {
    ll ans = all;
    int m = t / 2 + 1 - max(1, t - K);
    int sign = 1;
    for (int l = 1; l <= min(N / 2, m); l++) {
      ans -=
          sign * comb_mod(N + K - 1 - 2 * l, K - 1, P) * comb_mod(m, l, P) % P;
      sign *= -1;
      ans += P;
      ans %= P;
    }
    cout << ans << endl;
  }

  return 0;
}

