template <typename _Tp> _Tp gcd(const _Tp &a, const _Tp &b) {
  return (!b) ? a : gcd(b, a % b);
}
template <typename _Tp> Finline _Tp abs(const _Tp &a) {
  return a >= 0 ? a : -a;
}
template <typename _Tp> Finline _Tp max(const _Tp &a, const _Tp &b) {
  return a < b ? b : a;
}
