  for (int i = 0; i < W; i++) {
    if (s[i] == '#')
      flg = false;
  }
  return flg;
}

bool IsH(string s[], int H, int k) {
  bool flg = true;
  for (int i = 0; i < H; i++) {
    if (s[i][k] == '#')
      flg = false;
  }
  return flg;
}

