#include <iostream>
#include <vector>

typedef long long ll;

using namespace std;

int main(void) {
  int N, K;
  cin >> N >> K;
  vector<int> x(N);
  for (int i = 0; i < N; i++) {
    cin >> x[i];
  }

  int d = INT_MAX;
  for (int i = 0; i < N - K + 1; i++) {
    int vmax = INT_MIN;
    int vmin = INT_MAX;
    for (int j = i; j < i + K; j++) {
      vmax = max(vmax, x[j]);
      vmin = min(vmin, x[j]);
    }
    int cr = abs(vmax) + abs(vmax - vmin);
    int cl = abs(vmin) + abs(vmax - vmin);
    d = min(d, min(cr, cl));
  }
  cout << d << endl;
}

