#include <bits/stdc++.h>
using namespace std;
#define rep(i, n) for (int i = 0; i < (int)(n); i++)
#define all(x) (x).begin(), (x).end()
typedef long long ll;
const int MOD = (int)1e9 + 7;

int main() {
  int n, k;
  cin >> n >> k;
  vector<int> c(n);
  rep(i, n) cin >> c[i];
  int index = distance(c.begin(), lower_bound(all(c), 0));
  // cout << index << endl;
  int count = (index < n && c[index] == 0) ? 1 : 0;
  int left = k - count;
  int ans = (index + k - 1 < n ? c[index + k - 1] : MOD);
  rep(i, min(index - 1, left)) {
    int t = index - i - 1;
    if (i == left) {
      ans = min(ans, abs(c[t]));
      continue;
    }
    int buf = left - i - 1;
    ans = min(ans, -2 * c[t] + c[index + buf - 1]);
  }
  cout << ans << endl;
}

