int main() {

  int N, K;
  int x[100];
  cin >> N >> K;
  for (int i = 0; i < N; i++)
    cin >> x[i];

  /* 連続したK個を訪れるのが最短で、N個から連続したK個の選び方はN-K+1通り
     一気に+行って-に行くかその逆が最小値なのでN-K+1通りについて全探索*/

  int ans = 1e+9;

  // -に行ってから+に行く場合
  for (int i = 0; i < N - K + 1; i++) {
    ans = min(ans, abs(x[i]) + x[i + K - 1] - x[i]);
  }
  // +に行ってから-に行く場合
  for (int i = N - 1; i >= K - 1; i--) {
    ans = min(ans, abs(x[i]) + x[i] - x[i - K + 1]);
  }

  cout << ans << endl;

  return 0;
}

