#include <set>
#include <string>
#include <vector>

using namespace std;
using LLONG = long long;
const LLONG MOD = 1000000007;

const int Dist(const vector<int> &values, const int num) {
  if (values.empty() || num == 0) {
    return 0;
  } else {
    return values[num - 1];
  }
}

int main() {
  int N, K;
  cin >> N >> K;
  vector<int> lefts, rights;
  for (int i = 0; i < N; ++i) {
    int candle;
    cin >> candle;
    if (candle == 0) {
      --K;
    } else if (candle < 0) {
      lefts.push_back(-candle);
    } else {
      rights.push_back(candle);
    }
  }
  sort(lefts.begin(), lefts.end());
  int minSum = MOD;
  for (int leftNum = max(0, K - static_cast<int>(rights.size())),
           n = lefts.size();
       leftNum <= n; ++leftNum) {
    int rightNum = K - leftNum;
    int sum = Dist(lefts, leftNum) + Dist(rights, rightNum);
    if (leftNum != 0 && rightNum != 0) {
      sum += min(lefts[leftNum - 1], rights[rightNum - 1]);
    }
    if (sum < minSum) {
      minSum = sum;
    }
  }
  cout << minSum << endl;
}

