#include <bits/stdc++.h>

using namespace std;
typedef long long ll;

int main() {
  int n, k;
  cin >> n >> k;
  vector<ll> v;

  ll x;
  for (int i = 0; i < n; i++) {
    cin >> x;
    v.push_back(x);
  }

  int idx = 0; // 初めて要素が正に変わるときのインデックス
  while (true) {
    if (v[idx] > 0 || idx == n - 1) {
      break;
    }
    idx++;
  }

  int posi_num = n - idx; // 正の座標の数
  int nega_num = n - posi_num;

  if (posi_num == 0) {
    cout << -v[0] << endl;
    return 0;
  } else if (nega_num == 0) {
    cout << v[k - 1] << endl;
    return 0;
  }

  ll res;
  int left, right;
  if (k <= posi_num) {
    left = idx;
    right = k + idx - 1;
    res = v[right];
  } else {
    left = idx - k + posi_num;
    right = posi_num + idx - 1;
    res = std::min(v[right] + 2 * (-v[left]), 2 * v[right] + (-v[left]));
  }

  while (right > idx || left >= 0) {
    left--;
    right--;

    res = std::min(
        res, std::min(v[right] + 2 * (-v[left]), 2 * v[right] + (-v[left])));
  }

  cout << res << endl;
}

