using namespace std;

using ll = long long;
using ull = unsigned long long;

const double PI = acos(-1);
const int inf = 2e9;
const ll INF = 2e18;
const ll MOD = 1e9 + 7;

#define sz(s) (s).size()
#define pb push_back
#define fi first
#define se second
#define REP(i, n) for (int i = 0; i < n; i++)
#define ALL(a) begin(a), end(a)

int main(void) {
  int N, K;
  cin >> N >> K;
  vector<int> X;
  REP(i, N) {
    int t;
    cin >> t;
    if (t == 0)
      K--;
    else
      X.pb(t);
  }
  if (X.size() == 0) {
    cout << 0 << endl;
    return 0;
  }
  N = X.size();
  int zero = 0;
  if (X[0] >= 0)
    zero = 0;
  else if (X[N - 1] < 0)
    zero = N;
  else {
    for (int i = 1; i < N; i++) {
      if (X[i - 1] < 0 && X[i] >= 0)
        zero = i;
    }
  }
  ll ans = inf;
  REP(i, N) {
    ll temp;
    if (i < zero) {
      int g = zero - i;
      int d = K - g;
      if (g > K)
        continue;
      if (g == K)
        temp = abs(X[i]);
      else
        temp = abs(X[i]) * 2 + X[zero + d - 1];
    } else {
      int g = i - zero + 1;
      int d = K - g;
      if (g > K)
        continue;
      if (g == K)
        temp = abs(X[i]);
      else
        temp = abs(X[i]) * 2 + abs(X[i - K + 1]);
    }
    ans = min(temp, ans);
  }
  cout << ans << endl;
  return 0;
}

