  }
  n++;

  // 右方向にi本ロウソクを点ける
  for (ll i = 1; i <= k; i++) {
    // 左方向でk-i本ロウソクを点ける
    ll j = k - i;
    // もし範囲からはみ出る本数が指定されるなら飛ばす
    if (sp + i >= n || sp - j < 0)
      continue;
    // 右方向にi本ロウソクを点ける
    ll calc = x[sp + i];
    // 左方向に進む必要があるなら戻る分も考慮して移動量を算出
    if (j >= 1)
      calc += x[sp + i] + x[sp - j];
    // 最小値の更新
    chmin(ans, calc);
  }

  // 左方向にi本ロウソクを点ける
  for (ll i = 1; i <= k; i++) {
    // 右方向でk-i本ロウソクを点ける
    ll j = k - i;
    // もし範囲からはみ出る本数が指定されるなら飛ばす
    if (sp + j >= n || sp - i < 0)
      continue;
    // 左方向にi本ロウソクを点ける
    ll calc = x[sp - i];
    // 右方向に進む必要があるなら戻る分も考慮して移動量を算出
    if (j >= 1)
      calc += x[sp - i] + x[sp + j];
    // 最小値の更新
    chmin(ans, calc);
  }

  // 最小値の出力
  cout << ans << endl;
}

