using namespace std;

#define ll long long
#define inf numeric_limits<ll>::max() / 2

int main() {
  int n, k;
  cin >> n >> k;
  vector<int> seq;
  for (int i = 0; i < n; i++) {
    int ai;
    cin >> ai;
    seq.push_back(ai);
  }

  int inx0 = upper_bound(seq.begin(), seq.end(), 0) - seq.begin();
  inx0--;

  if (inx0 >= 0 && seq[inx0] == 0) {
    k--;
    seq.erase(find(seq.begin(), seq.end(), 0));
    inx0--;
    if (k == 0) {
      cout << 0 << endl;
      return 0;
    }
  }

  vector<ll> litl, litr;

  if (inx0 >= 0) {
    litl.push_back(abs(seq[inx0]));
  }

  for (int i = inx0 - 1; i >= 0; i--) {
    litl.push_back(litl.back() + seq[i + 1] - seq[i]);
  }

  if (inx0 + 1 < seq.size()) {
    litr.push_back(seq[inx0 + 1]);
  }

  for (int i = inx0 + 2; i < seq.size(); i++) {
    litr.push_back(litr.back() + seq[i] - seq[i - 1]);
  }

  ll sol = inf;
  sol = min(sol, (litr.size() >= k ? litr[k - 1] : inf));
  sol = min(sol, (litl.size() >= k ? litl[k - 1] : inf));

  for (int div = 1; div < seq.size() - 1; div++) {
    int oth = k - div;
    if (div > litl.size() || oth > litr.size()) {
      continue;
    }

    sol = min(sol, 2 * litl[div - 1] + litr[oth - 1]);
    sol = min(sol, litl[div - 1] + 2 * litr[oth - 1]);
  }

  cout << sol << endl;

  return 0;
}

