#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
const int maxn = 1e5;
ll a[maxn + 10];
ll zheng[maxn + 10];
ll fu[maxn + 10];
int n, k;
int main() {
  while (~scanf("%d%d", &n, &k)) {
    int da = 0;
    int xi = 0;
    for (int i = 0; i <= n; i++) {
      zheng[i] = 0;
      fu[i] = 0;
    }
    for (int i = 1; i <= n; i++) {
      scanf("%lld", &a[i]);
      if (a[i] >= 0) {
        zheng[++da] = a[i];
      }
    }
    for (int i = n; i >= 1; i--) {
      if (a[i] < 0) {
        fu[++xi] = -a[i];
      }
    }
    ll ans = 1e18;

    for (int i = k - max(da, xi); i <= k && i <= min(da, xi); i++) {
      if (da <= xi) {
        ll tmp = zheng[i] * 2 + fu[k - i];
        ans = min(ans, tmp);
        tmp = fu[k - i] * 2 + zheng[i];
        ans = min(ans, tmp);
      } else {
        ll tmp = fu[i] * 2 + zheng[k - i];
        ans = min(ans, tmp);
        tmp = zheng[k - i] * 2 + fu[i];
        ans = min(ans, tmp);
      }
    }
    cout << ans << endl;
  }
  return 0;
}

