#include <bits/stdc++.h>
using namespace std;

int main(void) {
  long long N, K;
  cin >> N >> K;
  vector<long long> x;
  vector<long long> y;
  for (long long i = 0; i < N; i++) {
    long long a;
    cin >> a;
    if (a >= 0) {
      x.push_back(a);
    } else {
      y.push_back(a * (-1));
    }
  }
  sort(x.begin(), x.end());
  sort(y.begin(), y.end());
  long long countx = 0;
  long long county = 0;
  long long preans;

  long long ans;
  if (x.size() > K) {
    ans = x[K - 1];
    countx = K;
  } else {
    if (x.size() == 0) {
      ans = y[K - 1];
      cout << ans << endl;
      exit(0);
    }
    if (y.size() == 0) {
      ans = x[K - 1];
      cout << ans << endl;
      exit(0);
    }
    ans = min(2 * x[x.size() - 1] + y[K - x.size() - 1],
              2 * y[K - x.size() - 1] + x[x.size() - 1]);
    countx = x.size();
    county = K - x.size();
  }
  for (long long i = 0; i < countx - 1; i++) {
    if (county < y.size()) {
      preans = min(2 * x[x.size() - 2 - i] + y[K - x.size() + i],
                   2 * y[K - x.size() + i] + x[x.size() - 2 - i]);
      if (ans > preans) {
        ans = preans;
      }
    } else {
      break;
    }
  }
  cout << ans << endl;
}

