#include "bits/stdc++.h"
using namespace std;

//

#define RESIDUE(u, v) (capacity[u][v] - flow[u][v])
#define RCOST(u, v) (cost[u][v] + h[u] - h[v])
#define FOR(i, a, b) for (int i = (a); i < (b); ++i)
#define REP(i, n) FOR(i, 0, n)
#define LL long long

int n, k;
LL x[100000];
int zp;

int main() {
  cin >> n >> k;
  REP(i, n)
  cin >> x[i];
  LL ret = LLONG_MAX;
  REP(i, n) {
    if (x[i] >= 0) {
      zp = i;
      break;
    }
  }

  if (x[n - 1] <= 0) {
    ret = -x[n - k];
    cout << ret << endl;
    return 0;
  }
  if (x[0] >= 0) {
    ret = x[k - 1];
    cout << ret << endl;
    return 0;
  }

  if (n - zp >= k)
    ret = min(ret, x[zp + k - 1]);
  if (zp >= k || (x[zp] == 0 && zp + 1 >= k)) {
    ret = min(ret, -x[zp - k]);
    if (x[zp] == 0 && zp + 1 >= k)
      ret = min(ret, -x[zp + 1 - k]);
  }
  REP(i, n) {
    if (x[i] <= 0 && x[i + k - 1] >= 0) {
      ret = min(min(ret, x[i + k - 1] - 2 * x[i]), 2 * x[i + k - 1] - x[i]);
    }
  }

  cout << ret << endl;
  return 0;
}

