  os << "}";
  return os;
}

void solve() {
  ll N, K;
  cin >> N >> K;
  vll x(N);
  for (auto &a : x)
    cin >> a;
  if (N == K) {
    out(min(abs(x[N - 1]) + abs(x[N - 1] - x[0]),
            abs(x[0]) + abs(x[N - 1] - x[0])));
    return;
  }
  vll dif(N - K);
  FOR(i, 0, N - K) { dif[i] = abs(x[i + K - 1] - x[i]); }
  ll ans = INF;
  FOR(i, 0, N - K) {
    ans = min(ans, dif[i] + abs(x[i]));
    ans = min(ans, dif[i] + abs(x[i + K - 1]));
  }
  out(ans);
}

signed main() {
  fast_io;
  cout << setprecision(20) << fixed;
  solve();
  return 0;
}

