#include <bits/stdc++.h>
#include <iostream>
using namespace std;

typedef long long LL;

int main() {
  int N, K;
  cin >> N >> K;
  vector<LL> pos_c, neg_c;
  LL c;
  for (LL i = 0; i < N; i++) {
    cin >> c;
    if (c > 0)
      pos_c.push_back(c);
    else if (c < 0)
      neg_c.push_back(abs(c));
    else
      K--;
  }

  int s_pos_c = pos_c.size();
  int s_neg_c = neg_c.size();
  LL cost = 1e9;
  sort(neg_c.begin(), neg_c.end());
  if (s_pos_c > 0 and s_neg_c > 0) {

    for (LL i = max(0, K - s_neg_c - 1); i < s_pos_c; i++)
      cost = min(cost, pos_c[i] + 2 * neg_c[K - i - 2]);

    for (LL i = max(0, K - s_pos_c - 1); i < s_neg_c; i++)
      cost = min(cost, neg_c[i] + 2 * pos_c[K - i - 2]);

  } else if (s_pos_c > 0)
    cost = pos_c[K - 1];
  else
    cost = neg_c[K - 1];

  cout << cost;

  return 0;
}

