#include <bits/stdc++.h>

typedef long long int ll;
typedef unsigned long long int ull;
#define rep(i, n) for (auto i = 0; i < (n); i++)

using namespace std;

int main() {
  ios::sync_with_stdio(0);
  cin.tie(0);

  int n, k;
  cin >> n >> k;
  vector<ll> x(n);
  rep(i, n) cin >> x[i];

  int idl = 0;
  int idr = 0;
  rep(i, n) {
    if (x[i] <= 0)
      idl = i;
    if (0 <= x[i]) {
      idr = i;
      break;
    }
  }

  if (idl == idr) {
    k--;
    idl--;
    idr++;
  }

  if (k == 0) {
    cout << 0 << endl;
    return 0;
  }

  vector<pair<int, ll>> left(idl + 1);
  rep(i, idl + 1) { left[i] = {idl - i + 1, (ll)abs(x[i])}; }

  vector<pair<int, ll>> rr(n - idr + 5);
  for (int i = idr; i < n; i++) {
    rr[i - idr] = {i - idr + 1, (ll)abs(x[i])};
  }

  ll minn = LLONG_MAX;
  rep(l, idl + 1) {
    if (left[l].first == k)
      minn = min(minn, left[l].second);
  }
  rep(r, n - idr) {
    if (rr[r].first == k)
      minn = min(minn, rr[r].second);
  }

  rep(l, idl + 1) {
    if (k < left[l].first)
      break;
    rep(r, n - idr) {
      if (k < left[l].first + rr[r].first)
        break;
      if (left[l].first + rr[r].first < k)
        continue;

      ll cost = left[l].second + rr[r].second;
      cost += min(left[l].second, rr[r].second);
      minn = min(minn, cost);
    }
  }

  cout << minn << endl;

  return 0;
}

