    ll l = v[i], r = v[i + k - 1];
    if (l <= 0 and r >= 0) {
      upmin(ans, -l + r + min(-l, r));
    } else if (l <= 0 and r <= 0) {
      upmin(ans, -l);
    } else {
      upmin(ans, r);
    }
  }

  cout << ans;

  return 0;
}

