// Solution：二分答案。
#include <bits/stdc++.h>
inline int read() {
  int s = 0, w = 1;
  char ch = getchar();
  while (ch < '0' || ch > '9') {
    if (ch == '-')
      w = -1;
    ch = getchar();
  }
  while (ch >= '0' && ch <= '9')
    s = s * 10 + ch - '0', ch = getchar();
  return s * w;
}
using namespace std;
int n = read(), a[1000003];
int tree[2000003];
int find(int x) {
  int res = 0;
  while (x)
    res += tree[x], x -= x & (-x);
  return res;
}
int add(int x) {
  while (x <= 2000000)
    ++tree[x], x += x & (-x);
}
bool check(int x) {
  long long sum = 0;
  int k = 1000000;
  memset(tree, 0, sizeof(tree)), add(1000000);
  for (int i = 1; i <= n; i++) {
    if (a[i] >= x)
      ++k;
    else
      --k;
    sum += find(k), add(k);
  }
  return sum >= 1ll * n * (n + 1) / 4;
}
int main() {
  for (int i = 1; i <= n; i++)
    a[i] = read();
  if (n == 1)
    printf("%d\n", a[1]), exit(0);
  int l = 0, r = 1000000001, ans = 0;
  while (l <= r) {
    int mid = (l + r) / 2;
    if (check(mid))
      ans = mid, l = mid + 1;
    else
      r = mid - 1;
  }
  printf("%d\n", ans);
  return 0;
}

