#include <bits/stdc++.h>
using namespace std;
typedef long long ll;

struct BIT {
  int size;
  vector<ll> t;
  void re() {
    for (int i = 0; i < (int)t.size(); i++)
      t[i] = 0;
  }
  void init(int n) {
    size = n;
    t.resize(n, 0);
  }

  void add(int i, ll x) {
    i++;
    while (i < size) {
      t[i] += x;
      i += (i & -i);
    }
  }

  ll sum(int i) {
    i++;
    ll res = 0;
    while (i) {
      res += t[i];
      i -= (i & -i);
    }
    return res;
  }
};

ll N;
ll a[100005];

ll b[100005];

BIT T;

ll Count(ll x) {
  T.re();
  for (int i = 0; i < N; i++)
    b[i] = (a[i] <= x ? 1 : -1);

  ll res = 0;
  ll sum = 150000;
  T.add(sum, 1);

  for (int i = 0; i < N; i++) {
    sum += b[i];
    res += T.sum(sum - 1);
    T.add(sum, 1);
  }
  return res;
}

int main() {

  T.init(200005);

  cin >> N;
  for (int i = 0; i < N; i++)
    cin >> a[i];

  ll total = (N + 1) * N / 2;
  ll target = total / 2 + 1;

  ll left = 1, right = 1e9, mid;
  while (left < right) {
    mid = (left + right) / 2;
    if (Count(mid) < target)
      left = mid + 1;
    else
      right = mid;
  }

  cout << left << endl;
  return 0;
}

