    for (int i = 0; i <= size[u]; ++i)
      for (int j = 0; j <= size[v]; ++j)
        Dec(tmp[i + j] += 1ll * dp[u][i] * dp[v][j] % mod);
    size[u] += size[v];
    for (int i = 0; i <= size[u]; ++i)
      dp[u][i] = tmp[i], tmp[i] = 0;
  }
  for (int i = 1; i <= size[u]; ++i)
    Inc(dp[u][0] -= 1ll * dp[u][i] * g[i] % mod);
}

