const int L = 5001;
const LL M = 1e9 + 7;
vector<int> dp[L][2];
vector<int> es[L];

LL g[L];
vector<LL> buf[2];
void f(int r, int p, vector<LL> u[2]) {
  u[0].push_back(0);
  u[0].push_back(1);
  u[1].push_back(0);
  u[1].push_back(0);
  for (auto c : es[r]) {
    if (c == p)
      continue;
    vector<LL> v[2];
    f(c, r, v);
    for (int i = 0; i < 2; i++) {
      buf[i].clear();
      buf[i].resize(u[i].size() + v[i].size());
    }
    for (int i = 0; i < 2; i++) {
      for (int j = 0; j < 2; j++) {
        for (int k = 1; k < u[i].size(); k++) {
