using namespace std;

const int N = 3e3 + 5;
const int mod = 1e9 + 7;
int _w;

int pls(int a, int b) { return a + b >= mod ? a + b - mod : a + b; }
int mns(int a, int b) { return a - b < 0 ? a - b + mod : a - b; }

int f[N][N], g[N], h[N], n, head[N], eidx, siz[N];
struct Edge {
  int nxt, to;
} edge[N << 1];

void addedge(int u, int v) {
  edge[++eidx] = (Edge){head[u], v};
  head[u] = eidx;
  edge[++eidx] = (Edge){head[v], u};
  head[v] = eidx;
}

void dfs(int u, int fa = 0) {
  siz[u] = 1, f[u][1] = 1;
  for (int e = head[u], v; e; e = edge[e].nxt)
    if ((v = edge[e].to) != fa) {
      dfs(v, u);
      for (int i = 0; i <= siz[u]; ++i)
        for (int j = 0; j <= siz[v]; ++j)
          g[i + j] = pls(g[i + j], 1LL * f[u][i] * f[v][j] % mod);
      siz[u] += siz[v];
      for (int i = 0; i <= siz[u]; ++i)
        f[u][i] = g[i], g[i] = 0;
    }
  int tmp = 0;
  for (int i = 0; i <= siz[u]; i += 2)
    tmp = mns(tmp, 1LL * h[i] * f[u][i] % mod);
  f[u][0] = tmp;
}

