// ある整数の約数列挙
vector<long long> divisors(long long n) {
  vector<long long> res;
  for (long long i = 1; i * i <= n; ++i) {
    if (n % i != 0)
      continue;
    res.push_back(i);
    if (n / i == i)
      continue; // 上の行で追加済み。
    res.push_back(n / i);
  }
  sort(res.begin(), res.end());
  return res;
}

// 素因数分解
map<long long, long long> prime_factors(long long n) {
  map<long long, long long> res;
  if (n == 1) { // n=1 の素因数分解は n^1
    res[n] = 1;
    return res;
  }
  for (long long i = 2, _n = n; i * i <= _n; ++i) {
    while (n % i == 0) {
      ++res[i]; // 素数i^{res[i]}
      n /= i;
    }
  }
  if (n != 1)
    res[n] = 1;
  return res;
}

// 繰り返し 2 乗法
long long modpow(long long a, long long n) {
  long long res = 1;
  while (n > 0) {
    if (n & 1)
      res = res * a % MOD;
    a = a * a % MOD;
    n >>= 1;
  }
  return res;
}

// 逆元を求める. a と m は互いに素であることが要請される.
long long modinv(long long a, long long m) {
  long long b = m, u = 1, v = 0;
  while (b) {
    long long t = a / b;
    a -= t * b;
    swap(a, b);
    u -= t * v;
    swap(u, v);
  }
  u %= m;
  if (u < 0)
    u += m;
  return u;
}

long long fac[MAXN], finv[MAXN], inv[MAXN];

// 前処理 O(n)
void math_init() {
  fac[0] = fac[1] = 1;
  finv[0] = finv[1] = 1;
  inv[1] = 1;
  for (long long i = 2; i < MAXN; i++) {
    fac[i] = fac[i - 1] * i % MOD;
    inv[i] = MOD - inv[MOD % i] * (MOD / i) % MOD;
    finv[i] = finv[i - 1] * inv[i] % MOD;
  }
}

// 二項係数計算 O(1)
long long COM(long long n, long long k) {
  if (n < k)
    return 0;
  if (n < 0 || k < 0)
    return 0;
  return fac[n] * (finv[k] * finv[n - k] % MOD) % MOD;
}

// before your coding, you have to write a line "math_init()"

vector<vector<int>> g;
lint sub[5001] = {}; // sub[i] := i 頂点を i / 2 組に分ける通り数
using Table = vector<vector<lint>>;

Table merge(const Table &dpl, const Table &dpr) {

  int n = dpl.size();
  int m = dpr.size();

  Table dp(n + m - 1, vector<lint>(2, 0));

  if (dpr.size() > dpl.size()) {
    for (int il = 0; il < n; il++) {
      for (int kl = 0; kl < 2; kl++) {
        if (dpl[il][kl] == 0)
          continue;
        for (int ir = 0; ir < m; ir++) {
          for (int kr = 0; kr < 2; kr++) {
            if (dpr[ir][kr] == 0)
              continue;

            // マージする
            (dp[il + ir][(kl + kr) % 2] += dpl[il][kl] * dpr[ir][kr]) %= MOD;

            // r はカット
            (dp[il][(kl + kr + 1) % 2] +=
             dpl[il][kl] * dpr[ir][kr] % MOD * sub[ir]) %= MOD;
          }
        }
      }
    }
  } else {
    for (int ir = 0; ir < m; ir++) {
      for (int kr = 0; kr < 2; kr++) {
        if (dpr[ir][kr] == 0)
          continue;
        for (int il = 0; il < n; il++) {
          for (int kl = 0; kl < 2; kl++) {
            if (dpr[il][kl] == 0)
              continue;

            // マージする
            (dp[il + ir][(kl + kr) % 2] += dpl[il][kl] * dpr[ir][kr]) %= MOD;

            // r はカット
            (dp[il][(kl + kr + 1) % 2] +=
             dpl[il][kl] * dpr[ir][kr] % MOD * sub[ir]) %= MOD;
          }
        }
      }
    }
  }

  return dp;
}

Table dfs(int cur, int par = -1) {
  Table dp(2, vector<lint>(2, 0));
  dp[1][0] = 1;
  dp[1][1] = 0;
  for (auto nxt : g[cur]) {
    if (nxt == par)
      continue;
    Table cdp = dfs(nxt, cur);
    dp = merge(dp, cdp);
  }

  return dp;
}

int main() {

  ios::sync_with_stdio(false);
  cin.tie(nullptr);

  math_init();

  int n;
