const int N = 5003, M = 1000000007;
int n, to[N * 2], hd[N * 2], lk[N], cnt, f[N][N], sz[N], co[N];
inline void add(int u, int v) { to[++cnt] = v, hd[cnt] = lk[u], lk[u] = cnt; }
int u, v;
void dfs(int x, int y) {
  f[x][1] = sz[x] = 1;
  for (int s, i = lk[x]; i; i = hd[i])
    if ((s = to[i]) ^ y) {
      dfs(s, x);
      for (int j = sz[x] + sz[s]; j; j--) {
        f[x][j] = (M - 1ll * f[x][j] * f[s][0]) % M;
        for (int k = 1; k <= sz[s] && k <= j; k++)
          f[x][j] = (f[x][j] + 1ll * f[x][j - k] * f[s][k]) % M;
      }
      sz[x] += sz[s];
    }
  for (int i = 2; i <= sz[x]; i += 2)
    f[x][0] = (f[x][0] + 1ll * f[x][i] * co[i]) % M;
}
