void solve(int x, int y) {
  dp[x][siz[x] = 1] = 1;
  for (int i : edge[x]) {
    if (i == y)
      continue;
    solve(i, x);
    for (int j = siz[x] + siz[i]; j; j--) {
      ll t = 0;
      for (int k = max(0, j - siz[x]); k <= max(siz[i], j); k++) {
        (t += dp[i][k] * dp[x][j - k]) %= MOD;
