using namespace std;
using ll = int64_t;

constexpr ll MOD = (ll)(1e9 + 7);
constexpr ll MAX = 5000;

ll N, dp[MAX + 1][MAX + 1][2], dp2[MAX + 1][2];
vector<vector<ll>> e;
vector<ll> g, sz;

void rec(ll v, ll p) {
  sz[v] = 1;
  dp[v][1][0] = 1;
  for (ll i : e[v])
    if (i != p) {
      rec(i, v);
      memset(dp2, 0, sizeof(dp2));
      for (ll x = 1; x <= sz[v]; x++)
        for (ll y = 1; y <= sz[i]; y++) {
          // v-i間の辺を取り除く場合
          (dp2[x][0] += dp[v][x][0] * dp[i][y][1] % MOD * g[y] +
                        dp[v][x][1] * dp[i][y][0] % MOD * g[y]) %= MOD;
          (dp2[x][1] += dp[v][x][0] * dp[i][y][0] % MOD * g[y] +
                        dp[v][x][1] * dp[i][y][1] % MOD * g[y]) %= MOD;
          // 取り除かない場合
          (dp2[x + y][0] +=
           dp[v][x][0] * dp[i][y][0] + dp[v][x][1] * dp[i][y][1]) %= MOD;
          (dp2[x + y][1] +=
           dp[v][x][0] * dp[i][y][1] + dp[v][x][1] * dp[i][y][0]) %= MOD;
        }
      swap(dp[v], dp2);
      sz[v] += sz[i];
    }
}

int main() {
  cin.tie(0);
  ios::sync_with_stdio(0);

  cin >> N;

  sz.resize(N, 0);

  g.resize(N + 1, 0);
  g[2] = 1;
  for (ll i = 4; i <= MAX; i++) {
    g[i] = g[i - 2] * (i - 1) % MOD;
  }

  e.resize(N + 1);
  for (ll i = 0; i < N - 1; i++) {
    ll x, y;
    cin >> x >> y;
    x--, y--;
    e[x].push_back(y), e[y].push_back(x);
  }

  rec(0, -1);

  ll ans = 0;
  for (ll i = 1; i <= N; i++) {
    (ans += (dp[0][i][0] - dp[0][i][1] + MOD) % MOD * g[i]) %= MOD;
  }
  cout << ans << endl;
}

