const int MAXP = 1.1e5;
const int MAXE = 1.1e5;
int P, E;
int X[MAXP];
int Y[MAXE];

int D[MAXP][2];

const int MAXN = MAXP;
int N[2];
int vals[2][MAXN];
vector<int> adj[MAXN];

num bit[MAXN];
num query(int a) {
  assert(0 <= a && a <= N[1] + 1);
  num res = 0;
  for (; a; a -= a & -a) {
    res += bit[a];
  }
  return res;
}

void update(int a, num v) {
  assert(0 <= a && a <= N[1]);
  for (a++; a <= N[1] + 1; a += a & -a) {
    bit[a] += v;
  }
}

