    cin >> Y[j];
  }

  for (int i = 0, j = 0; i < P; i++) {
    while (j < E && Y[j] < X[i]) {
      assert(Y[j] != X[i]);
      j++;
    }
    assert(j == E || Y[j] > X[i]);
    if (j == 0) {
