#include <bits/stdc++.h>
using namespace std;

const int64_t MOD = 1e9 + 7;
void add_mod(int64_t &a, int64_t b) { a = (a + b) % MOD; }

template <typename T> struct BIT {
  int n;
  vector<T> dat;

  BIT(int n = 0) : n(n) { initialize(n); }

  void initialize(int n) {
    dat.resize(n);
    for (int i = 0; i < n; i++)
      dat[i] = 0;
  }

  T sum(int i) {
    T s = 0;
    while (i >= 0) {
      add_mod(s, dat[i]);
      i = (i & (i + 1)) - 1;
    }
    return s;
  }

  void add(int i, T x) {
    while (i <= n) {
      add_mod(dat[i], x);
      i |= i + 1;
    }
  }
};

int main() {
  int N, M;
  cin >> N >> M;
  vector<int> X(N), Y(M);
  for (int i = 0; i < N; i++)
    cin >> X[i];
  for (int i = 0; i < M; i++)
    cin >> Y[i];

  vector<pair<int, int>> pts;
  vector<int> ys = {0};
  for (int i = 0, j = 0; i < N; i++) {
    if (X[i] < Y[0] || Y[M - 1] < X[i])
      continue;
    while (Y[j] < X[i])
      j++;
    pts.push_back({X[i] - Y[j - 1], -(Y[j] - X[i])});
    ys.push_back(Y[j] - X[i]);
  }
  if (pts.size() == 0) {
    cout << 1 << endl;
    return 0;
  }

  sort(pts.begin(), pts.end());
  sort(ys.begin(), ys.end());
  pts.erase(unique(pts.begin(), pts.end()), pts.end());
  ys.erase(unique(ys.begin(), ys.end()), ys.end());

  BIT<int64_t> bit(ys.size());
  bit.add(0, 1);

  for (auto p : pts) {
    int y = -p.second;
    int i = lower_bound(ys.begin(), ys.end(), y) - ys.begin();
    if (i > 0)
      bit.add(i, bit.sum(i - 1));
  }
  cout << bit.sum(ys.size() - 1) << endl;
  return 0;
}

