#include <algorithm>
#include <cmath>
#include <cstdio>
#include <cstring>
#include <iostream>
#include <map>
#include <queue>
#include <set>
#include <stack>
#include <unordered_map>
#include <vector>

using namespace std;
typedef long long ll;
typedef pair<int, int> P;
#define rep(i, n) for (ll i = 0; i < (ll)(n); i++)
#define repr(i, n) for (ll i = n - 1; i >= 0; i--)
#define repone(i, n) for (ll i = 1; i <= (ll)(n); i++)
#define each(i, mp) for (auto i : mp)
#define FOR(i, m, n) for (ll i = m; i < n; i++)
const int dx[4] = {1, 0, -1, 0}, dy[4] = {0, 1, 0, -1};
const ll mod = 1000000007;
const ll inf = ll(1e9);
const ll half_inf = ll(1e5);
const ll ll_inf = ll(1e9) * ll(1e9);

ll n, m, Q;
vector<ll> l, r;
vector<ll> p, q;

void solve() {
  ll d[n + 1][n + 1];
  fill(d[0], d[n + 1], 0);
  rep(i, m) d[l[i]][r[i]]++;
  ll dp[n + 1][n + 1];
  fill(dp[0], dp[n + 1], 0);
  repone(i, n) {
    repone(j, n) { dp[i][j] = dp[i][j - 1] + d[i][j]; }
  }
  rep(i, Q) {
    ll ans = 0;
    for (ll j = p[i]; j <= q[i]; ++j)
      ans += dp[j][q[i]] - dp[j][p[i] - 1];
    cout << ans << endl;
  }
}

int main() {
  cin >> n >> m >> Q;
  l.resize(n), r.resize(n);
  p.resize(Q), q.resize(Q);
  rep(i, m) cin >> l[i] >> r[i];
  rep(i, Q) cin >> p[i] >> q[i];
  solve();
  return 0;
}

