void BIT_upd(int p, int val) { // O(log(n))
  p++;
  for (; p <= N; p += p & -p)
    BIT[p] += val;
}

// devuelve la suma de [0,p]
ll BIT_sum(int p) { // O(log(n))
  ll ret = 0;
  p++;
  for (; p; p -= p & -p)
