  sum = vector<vector<int>>(N + 1, vector<int>(N + 1, 0));
  for (int i = 0; i < M; ++i) {
    sum[L[i]][R[i]]++;
  }

  for (int i = 0; i <= N; ++i) {
    for (int j = 1; j <= N; ++j) {
      sum[i][j] += sum[i - 1][j];
      sum[i][j] += sum[i][j - 1];
      sum[i][j] -= sum[i - 1][j - 1];
    }
  }
}

