#include <algorithm>
#include <iostream>
#include <vector>

#define INF 1000000000
#define ALL(a) (a).begin(), (a).end()

using namespace std;
typedef long long ll;

int main() {
  vector<vector<int>> vvi(600);
  int memo[600][600] = {};
  for (int i = 0; i < 600; i++) {
    for (int j = 0; j < 600; j++) {
      memo[i][j] = -1;
    }
  }

  int n, m, q;
  cin >> n >> m >> q;

  // vvi[l][i] := 左端lの区間の右端
  for (int i = 0; i < m; i++) {
    int l, r;
    cin >> l >> r;
    vvi[l].push_back(r);
  }
  for (int i = 0; i < 500; i++) {
    vvi[i].push_back(INF);
    sort(ALL(vvi[i]));
  }

  for (int i = 0; i < q; i++) {
    ll ans = 0;
    int l, r;
    cin >> l >> r;

    // 右端をl～rまで動かす
    for (int j = l; j <= r; j++) {
      if (memo[j][r] != -1) {
        ans += memo[j][r];
        continue;
      }
      //[j, r]に含まれる区間の個数を数える
      // INFになってしまったらvvi[j]の全要素が[j, r]に含まれる
      // memo[j][r] = [j, r]に含まれる区間の個数として、再利用する
      int idx = upper_bound(ALL(vvi[j]), r) - vvi[j].begin();
      if (vvi[j][idx] != INF) {
        ans += idx;
        memo[j][r] = idx;
      } else {
        ans += vvi[j].size() - 1;
        memo[j][r] = vvi[j].size() - 1;
      }
    }
    cout << ans << endl;
  }
  return 0;
}

