    memset(cum, 0, sizeof cum);
    memset(x, 0, sizeof x);

    REP(i, m) {
      cin >> l >> r;
      ++x[l][r];
    }

    // O(n^2)
    REP(i, n)
    REP(j, n) { cum[i + 1][j + 1] = cum[i + 1][j] + x[i + 1][j + 1]; }

    // O(qn)
    REP(i, q) // q loops
    {
      cin >> l >> r;
      int sum{0};

      for (int j{l}; j <= r; j++) // n loops
        sum += cum[j][r] - cum[j][l - 1];

      cout << sum << endl;
    }

    // end solution solve_cumulative
  }

  // O(n^2 + q)
  void solve_2Dcumulative() {
    int l, r;
    int cum[MAX_N][MAX_N];
    memset(cum, 0, sizeof cum);

    REP(i, m) {
      cin >> l >> r;
      ++cum[l][r];
    }

    // O(n^2)
    REP(i, n)
    REP(j, n)
    cum[i + 1][j + 1] += cum[i + 1][j];
    // O(n^2)

    REP(i, n)
    REP(j, n)
    cum[i + 1][j + 1] += cum[i][j + 1];

    // O(q)
    REP(i, q) {
      cin >> l >> r;

      int ans{cum[r][r] + cum[l - 1][l - 1] - cum[l - 1][r] - cum[r][l - 1]};

      cout << ans << endl;
    }

    // end solution solve_2Dcumulative
  }

  void solve() { solve_cumulative(); }
};

int main() {
  D solution;
  solution.solve();

  return 0;
}

