int find_index(const vector<pair<int, int>> &v, int l) {
  if (v.back().first < l)
    return -1;
  if (v.front().first >= l)
    return 0;

  int lower = 0;
  int upper = v.size() - 1;
  do {
    int cur = (lower + upper) / 2;
    if (v[cur].first >= l)
      upper = cur;
    else
      lower = cur;
  } while (upper - lower > 1);
