#include <chrono>
#define ll long long
#define BUF 50
using namespace std;
ll MOD = 1e9 + 7;
ll A, B, H, N, M, L, K, P, Q, R, W, X, Y, Z;
ll ans = 0;

int main() {
  cin >> N;
  ll sum[BUF];
  sum[0] = 1, sum[1] = -2;
  for (int i = 2; i < 31; i++) {
    if (i & 1)
      sum[i] = sum[i - 2] - pow(2, i);
    else
      sum[i] = sum[i - 2] + pow(2, i);
  }
  vector<char> S(BUF, '0');
  while (N != 0) {
    int i = 0;
    if (N > 0) {
      while (N > sum[i]) {
        i++;
      }
      S[i] = '1';
      N -= pow(2, i);
    } else {
      while (N < sum[i]) {
        i++;
      }
      S[i] = '1';
      N += pow(2, i);
    }
  }
  bool flag = 0;
  for (int i = BUF - 1; i >= 0; i--) {
    if (S[i] == '1')
      flag = 1;
    if (flag)
      cout << S[i];
  }
  if (flag == 0)
    cout << 0 << endl;
}

