int main() {
  int n, i, ni = 2, mni = 1, a[10000];
  cin >> n;
  for (i = 0;; i++) {
    if (n % ni == 0) {
      a[i] = 0;
    } else {
      a[i] = 1;
      n -= mni;
    }
    if (n == 0) {
      break;
    }
    ni *= 2;
    mni *= -2;
  }
  for (; i >= 0; i--) {
    cout << a[i];
  }
}

